mapper.select:
select count(*)
from bbs
where refnum = #{bbsno}

mapper.update:
update bbs
set ansnum = ansnum + 1
where grpno = #{grpno}
and ansnum > #{ansnum}

update bbs
set wname = #{wname},
title = #{title},
content = #{content}

	,filename = #{filename},
	filesize = #{filesize}

where bbsno = #{bbsno}

mapper.select:
		select count(*)
		from bbs
		where bbsno= #{bbsno}
		and passwd = #{passwd}

mapper.update:
update bbs
set viewcnt = viewcnt +1
where bbsno = #{bbsno}

mapper.select:
		select bbsno, wname, title, viewcnt, wdate,indent, filename, r
		from(
		select bbsno, wname, title, viewcnt, wdate,indent, filename, rownum as r
		from(
		select bbsno, wname, title, viewcnt, indent, filename,
		to_char(wdate, 'yyyy-mm-dd') as wdate from bbs
		
			
				
					wname like '%'||#{word}||'%'
				
				
					title like '%'||#{word}||'%'
				
				
					content like '%'||#{word}||'%'
				
			
		
		order by grpno desc, ansnum
		) 
                                                    
)where r>=#{sno} and r<=#{eno}
